mapper.select:
select COUNT(1) AS total,inquiry_area AS area, return_season AS reason from erui_rfq.v_inq_rtn_reason
order by ${orderByClause}
GROUP BY inquiry_area ,return_season HAVING inquiry_area IS NOT NULL AND return_season IS NOT NULL

select COUNT(1) AS total,organization AS org, return_season AS reason from erui_rfq.v_inq_rtn_reason
order by ${orderByClause}
GROUP BY organization ,return_season HAVING organization IS NOT NULL AND return_season IS NOT NULL
