mapper.select:
SELECT  SUM(return_count) AS  sumCount,avg(return_count) AS  avgCount
FROM inquiry_count




  order by ${orderByClause}

SELECT COUNT(1) as count ,FROM_DAYS(TO_DAYS(rollin_time)) AS datetime FROM inquiry_count
order by ${orderByClause}
GROUP BY TO_DAYS(rollin_time)

select DISTINCT(organization)
from inquiry_count




  order by ${orderByClause}

select DISTINCT(inquiry_area)
from inquiry_count




  order by ${orderByClause}

SELECT COUNT(1) as proCount ,pro_category as proCategory FROM inquiry_count


      and rollin_time >= #{startTime}


       and rollin_time < #{endTime}


  group by pro_category HAVING pro_category IS  NOT  NULL  ORDER BY proCount DESC LIMIT 0,3;

select 

from inquiry_count
where id = #{id,jdbcType=BIGINT}
