mapper.select:
SELECT COUNT(*) AS skuCount, SUM(IFNULL(cate_count*quote_unit_price,0.0)) AS skuAmount, SUM(cate_count) AS cateCount, is_oil_gas AS isOil from erui_rfq.v_inquiry_sku
order by ${orderByClause}
GROUP BY is_oil_gas

SELECT COUNT(*) AS skuCount, plat_pro_category AS isPlat FROM erui_rfq.v_inquiry_sku
order by ${orderByClause}
GROUP BY plat_pro_category HAVING plat_pro_category IS NOT NULL

SELECT COUNT(*) AS proCount, pro_category AS proCategory FROM erui_rfq.v_inquiry_sku WHERE 1=1 AND quotation_num IN (SELECT quotation_num FROM erui_rfq.v_inquiry_count WHERE 1=1
and rollin_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
AND quoted_status IN
#{quote}
AND quotation_num IS NOT NULL ) group by pro_category HAVING pro_category IS NOT NULL ORDER BY proCount DESC LIMIT 0,3;

SELECT IFNULL(sum(IFNULL(quote_unit_price,0.00)*IFNULL(cate_count,0)),0) AS inqCatePrice, COUNT(*) AS inqCateCount, pro_category AS category FROM erui_rfq.v_inquiry_sku
order by ${orderByClause}
GROUP BY category HAVING category IS NOT NULL
