mapper.select:
SELECT SUM(cate_count) AS skuCount, is_oil_gas AS isOil from inquiry_sku
order by ${orderByClause}
GROUP BY is_oil_gas

SELECT
SUM(cate_count) AS proCount,
pro_category AS proCategory
FROM
inquiry_sku


    and rollin_time >= #{startTime}


    and rollin_time < #{endTime}


group by pro_category HAVING pro_category IS  NOT  NULL  ORDER BY proCount DESC LIMIT 0,3;

SELECT IFNULL(sum(IFNULL(quote_unit_price,0.00)*IFNULL(cate_count,0)),0) AS inqCatePrice, SUM(cate_count) AS inqCateCount, pro_category AS category FROM inquiry_sku
order by ${orderByClause}
GROUP BY category HAVING category IS NOT NULL
