mapper.select:
SELECT country, IFNULL(SUM(IFNULL(inquiry_count, 0)),0) AS inqCount, IFNULL(SUM(IFNULL(inquiry_amount, 0.00)),0.00) AS inqAmount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and country = #{country}
GROUP BY country HAVING country IS NOT NULL ORDER BY inqCount DESC LIMIT 1

SELECT country, IFNULL(SUM(IFNULL(bargain_count, 0)),0) AS ordCount, IFNULL(SUM(IFNULL(bargain_amount, 0.00)),0.00) AS ordAmount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and country = #{country}
GROUP BY country HAVING country IS NOT NULL ORDER BY ordCount DESC LIMIT 1

SELECT country, IFNULL(SUM(IFNULL(new_member_count, 0)),0) AS memberCount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and country = #{country}
GROUP BY country HAVING country IS NOT NULL ORDER BY memberCount DESC LIMIT 1

SELECT area, marketer, IFNULL(SUM(IFNULL(inquiry_count, 0)),0) AS inqCount, IFNULL(SUM(IFNULL(inquiry_amount, 0.00)),0.00) AS inqAmount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and area = #{area}
and marketer = #{marketer}
GROUP BY area,marketer HAVING area IS NOT NULL and marketer IS NOT NULL ORDER BY inqCount DESC LIMIT 1

SELECT area, marketer, IFNULL(SUM(IFNULL(bargain_count, 0)),0) AS ordCount, IFNULL(SUM(IFNULL(bargain_amount, 0.00)),0.00) AS ordAmount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and area = #{area}
and marketer = #{marketer}
GROUP BY area,marketer HAVING area IS NOT NULL and marketer IS NOT NULL ORDER BY ordCount DESC LIMIT 1

SELECT area, marketer, IFNULL(SUM(IFNULL(new_member_count, 0)),0) AS memberCount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and area = #{area}
and marketer = #{marketer}
GROUP BY area,marketer HAVING area IS NOT NULL and marketer IS NOT NULL ORDER BY memberCount DESC LIMIT 1

SELECT IFNULL(SUM(IFNULL(inquiry_count, 0)),0) AS inqCount, IFNULL(SUM(IFNULL(inquiry_amount, 0)),0) AS inqAmount, IFNULL(SUM(IFNULL(quote_count, 0)),0) AS quoteCount, IFNULL(SUM(IFNULL(bargain_count, 0)),0) AS ordCount, IFNULL(SUM(IFNULL(bargain_amount, 0)),0) AS ordAmount, IFNULL(SUM(IFNULL(new_member_count, 0)),0) AS memberCount FROM marketer_count where create_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
and area = #{area}
and country = #{country}
