mapper.select:
SELECT
FROM performance_assign WHERE start_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
AND three_level_department= #{country}

mapper.update:
UPDATE  performance_assign

    assign_status=#{state}

      , reject_reason=#{rejectReason}


WHERE
start_time BETWEEN
DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
AND  three_level_department= #{country}

update.foreach:
  UPDATE  performance_assign SET
  assign_status =#{item.assignStatus}

    , assign_rate =#{item.assignRate}


   , salesman_performance =#{item.salesmanPerformance}


   , reject_reason =#{item.rejectReason}

WHERE start_time=#{item.startTime} AND work_num=#{item.workNum} AND three_level_department=#{item.threeLevelDepartment}
