mapper.select:
SELECT name FROM erui_dict.country
WHERE deleted_flag='N' AND lang='zh' AND
bn IN (
      SELECT country_bn FROM erui_sys.country_member AS c
      WHERE c.employee_id=#{userId}
    )

SELECT p.area AS area ,p.execute_company AS country ,p.obtainer AS employee,s.station as job , SUM(IFNULL(p.project_amount,0))/10000 AS totalPerformance , SUM((CASE WHEN p.sign_company='易瑞' THEN IFNULL(p.project_amount,0) ELSE 0 END ))/10000 AS eruiPerformance, SUM((CASE WHEN p.sign_company!='易瑞' THEN IFNULL(p.project_amount,0) ELSE 0 END ))/10000 AS otherPerformance FROM performance_count AS p ,salesman AS s WHERE p.start_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
AND p.area= #{area}
AND p.execute_company = #{country}
AND p.obtainer=s.name_ch AND p.obtain_main_company=s.organization GROUP BY p.area , p.obtainer HAVING p.obtainer IS NOT NULL AND p.area IS NOT NULL ;

SELECT area ,execute_company AS country , buyer_code AS buyerCode FROM performance_count WHERE start_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
AND area= #{area}
AND execute_company = #{country}
AND buyer_code NOT IN ( SELECT buyer_code FROM performance_count WHERE start_time < DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") )
