mapper.select:
SELECT entryData.entryCount,entryData.entryAmount,outData.outCount,outData.outAmount FROM ( SELECT IFNULL(SUM(IFNULL(entry_count, 0)),0) AS entryCount, IFNULL(SUM(IFNULL(amounts, 0)),0) AS entryAmount FROM order_entry_count where 1=1
and storage_date BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
) AS entryData, ( SELECT IFNULL(SUM(IFNULL(pack_count, 0)),0) AS outCount, IFNULL(SUM(IFNULL(amounts, 0)),0) AS outAmount FROM order_outbound_count where 1=1
and outbound_date BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
) AS outData

SELECT entryData.entryCount-outData.outCount AS totalCount,entryData.entryAmount-outData.outAmount AS totalAmount FROM ( SELECT IFNULL(SUM(IFNULL(entry_count, 0)),0) AS entryCount, IFNULL(SUM(IFNULL(amounts, 0)),0) AS entryAmount FROM order_entry_count where 1=1
and storage_date < #{endTime}
) AS entryData, ( SELECT IFNULL(SUM(IFNULL(pack_count, 0)),0) AS outCount, IFNULL(SUM(IFNULL(amounts, 0)),0) AS outAmount FROM order_outbound_count where 1=1
and outbound_date < #{endTime}
) AS outData

mapper.insert:
insert into storage_organi_count (id, create_at, organi_name, 
  tray_num, product_num, remark
  )
values (#{id,jdbcType=INTEGER}, #{createAt,jdbcType=TIMESTAMP}, #{organiName,jdbcType=VARCHAR}, 
  #{trayNum,jdbcType=INTEGER}, #{productNum,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
  )
