mapper.select:
SELECT id, buyer_code AS buyerCode, country_name AS countryName, area_name AS areaName, acquiring_user_name as acquiringUserName, IFNULL(DATE_FORMAT(register_time, '%Y/%m/%d'),'') AS registerTime FROM erui_report.rep_buyer_country_info WHERE register_time IS NOT NULL
AND register_time >= #{registerStartTime}
AND register_time <= #{registerEndTime}

SELECT id, buyer_code AS buyerCode, country_name AS countryName, area_name AS areaName, acquiring_user_name as acquiringUserName, IFNULL(DATE_FORMAT(membership_time, '%Y/%m/%d'),'') AS membershipTime FROM erui_report.rep_buyer_country_info WHERE membership_time IS NOT NULL
AND membership_time >= #{membershipStartTime}
AND membership_time <= #{membershipEndTime}

SELECT id, buyer_code AS buyerCode, country_name AS countryName, area_name AS areaName, acquiring_user_name as acquiringUserName, IFNULL(DATE_FORMAT(apply_time, '%Y/%m/%d'),'') AS applyTime FROM erui_report.rep_buyer_country_info WHERE apply_time IS NOT NULL
AND apply_time >= #{applyStartTime}
AND apply_time <= #{applyEndTime}

SELECT area_bn, area_name, country_bn, country_name, COUNT(buyer_code) AS num FROM erui_report.rep_buyer_country_info WHERE membership_time IS NOT NULL
AND membership_time >= #{startTime}
AND membership_time <= #{endTime}
GROUP BY area_bn , area_name , country_bn , country_name ORDER BY 5 DESC
