mapper.select:
SELECT category, COUNT(DISTINCT id) AS num FROM (SELECT t1.category, t2.id AS id FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_APPROVING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3 WHERE t1.inquiry_id = t2.id AND t1.inquiry_id = t3.log_inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != ''
AND t2.area_bn = #{areaBn}
AND t2.country_bn = #{countryBn}
AND t2.trade_terms_bn = #{tradeTerms}
AND t2.`status` IN ('BIZ_DISPATCHING' , 'CC_DISPATCHING', 'BIZ_QUOTING', 'LOGI_DISPATCHING', 'LOGI_QUOTING', 'LOGI_APPROVING', 'BIZ_APPROVING', 'MARKET_APPROVING', 'MARKET_CONFIRMING', 'QUOTE_SENT', 'INQUIRY_CLOSED') UNION ALL SELECT t1.material_cat_name AS category, t2.id AS id FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_APPROVING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3 WHERE t1.inquiry_id = t2.id AND t1.inquiry_id = t3.log_inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t1.material_cat_name IS NOT NULL AND t1.material_cat_name != '' AND (t1.category IS NULL OR t1.category = '')
AND t2.area_bn = #{areaBn}
AND t2.country_bn = #{countryBn}
AND t2.trade_terms_bn = #{tradeTerms}
AND t2.`status` IN ('BIZ_DISPATCHING' , 'CC_DISPATCHING', 'BIZ_QUOTING', 'LOGI_DISPATCHING', 'LOGI_QUOTING', 'LOGI_APPROVING', 'BIZ_APPROVING', 'MARKET_APPROVING', 'MARKET_CONFIRMING', 'QUOTE_SENT', 'INQUIRY_CLOSED')) t GROUP BY category ORDER BY 2 DESC

SELECT category, COUNT(DISTINCT id) AS num FROM (SELECT t1.category, t4.id FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3, erui_rfq.final_quote t4 WHERE t1.inquiry_id = t2.id
AND t2.area_bn = #{areaBn}
AND t2.country_bn = #{countryBn}
AND t2.trade_terms_bn = #{tradeTerms}
AND t1.inquiry_id = t3.log_inquiry_id AND t2.id = t4.inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t4.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != '' AND t2.`status` IN ('MARKET_CONFIRMING' , 'QUOTE_SENT', 'INQUIRY_CLOSED') UNION ALL SELECT t1.material_cat_name AS category, t4.id FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3, erui_rfq.final_quote t4 WHERE t1.inquiry_id = t2.id
AND t2.area_bn = #{areaBn}
AND t2.country_bn = #{countryBn}
AND t2.trade_terms_bn = #{tradeTerms}
AND t1.inquiry_id = t3.log_inquiry_id AND t2.id = t4.inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t4.deleted_flag = 'N' AND t1.material_cat_name IS NOT NULL AND t1.material_cat_name != '' AND (t1.category IS NULL OR t1.category = '') AND t2.`status` IN ('MARKET_CONFIRMING' , 'QUOTE_SENT', 'INQUIRY_CLOSED')) AS t GROUP BY category ORDER BY 2 DESC

SELECT category, SUM(amount) / 10000 AS num FROM (SELECT t1.category, t1.qty * IFNULL(t2.exw_unit_price, 0) AS amount FROM erui_rfq.inquiry_item t1, erui_rfq.quote_item t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3,erui_rfq.inquiry t4 WHERE t1.id = t2.inquiry_item_id AND t1.inquiry_id = t4.id
AND t4.area_bn = #{areaBn}
AND t4.country_bn = #{countryBn}
AND t4.trade_terms_bn = #{tradeTerms}
AND t1.inquiry_id = t3.log_inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != '' UNION ALL SELECT t1.material_cat_name AS category, t1.qty * IFNULL(t2.exw_unit_price, 0) AS amount FROM erui_rfq.inquiry_item t1, erui_rfq.quote_item t2, (SELECT inquiry_id AS log_inquiry_id, MIN(out_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t3,erui_rfq.inquiry t4 WHERE t1.id = t2.inquiry_item_id AND t1.inquiry_id = t4.id
AND t4.area_bn = #{areaBn}
AND t4.country_bn = #{countryBn}
AND t4.trade_terms_bn = #{tradeTerms}
AND t1.inquiry_id = t3.log_inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t1.material_cat_name IS NOT NULL AND t1.material_cat_name != '' AND (t1.category IS NULL OR t1.category = '')) AS t GROUP BY category ORDER BY 2 DESC

SELECT t2.bn, t2.`name` as category, COUNT(t0.id) AS num FROM erui_rfq.inquiry t0, (SELECT inquiry_id FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_APPROVING' GROUP BY inquiry_id
HAVING MIN(out_at) BETWEEN #{startTime} and #{endTime}
) AS t1, erui_dict.country AS t2 WHERE t0.id = t1.inquiry_id AND t0.country_bn = t2.bn AND t2.lang = 'zh' GROUP BY t2.bn , t2.`name` ORDER BY 3 DESC limit 15

SELECT t2.bn, t2.`name` as category, COUNT(t4.id) AS num FROM erui_rfq.inquiry t0, (SELECT inquiry_id FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MAX(out_at) BETWEEN #{startTime} and #{endTime}
) AS t1, erui_dict.country AS t2, erui_rfq.final_quote t4 WHERE t0.id = t1.inquiry_id AND t0.country_bn = t2.bn AND t2.lang = 'zh' AND t0.id = t4.inquiry_id GROUP BY t2.bn , t2.`name` ORDER BY 3 DESC limit 15

SELECT t2.bn, t2.`name` as category, SUM(t3.total_quote_price) / 10000 AS num FROM erui_rfq.inquiry t0, (SELECT inquiry_id FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MAX(out_at) BETWEEN #{startTime} and #{endTime}
) AS t1, erui_dict.country AS t2, erui_rfq.final_quote t3 WHERE t0.id = t1.inquiry_id AND t0.country_bn = t2.bn AND t2.lang = 'zh' AND t0.id = t3.inquiry_id GROUP BY t2.bn , t2.`name` ORDER BY 3 DESC limit 15;
