mapper.select:
SELECT t1.org_id AS orgId, CASE t1.org_id WHEN 1 THEN '易瑞-钻完井设备事业部' WHEN 2 THEN '易瑞-工业品事业部' WHEN 3 THEN '易瑞-采油工程事业部' WHEN 4 THEN '易瑞' WHEN 5 THEN '油田设备' WHEN 6 THEN '康博瑞' WHEN 7 THEN '钻采' WHEN 8 THEN 'WEFIC' ELSE '其他' END AS `name`, SUM(t1.inquiryNum) AS inquiryNum, SUM(t1.orderNum) AS orderNum, CASE SUM(t1.inquiryNum) WHEN 0 THEN 0 ELSE SUM(t1.orderNum) / SUM(t1.inquiryNum) END AS rate FROM (SELECT CASE WHEN business_unit_id IN (9907) THEN 1 WHEN business_unit_id IN (9969) THEN 2 WHEN business_unit_id IN (9970) THEN 3 WHEN business_unit_id IN (9928 , 9929, 9931, 9930 ) THEN 4 WHEN business_unit_id IN (9904 , 9905, 9906, 9937) THEN 5 WHEN business_unit_id IN (9902 , 9917, 9938) THEN 6 WHEN business_unit_id IN (9903 , 9901, 9940) THEN 7 WHEN business_unit_id IN (9899 , 9916) THEN 8 ELSE 100 END AS org_id, 0 AS inquiryNum, COUNT(1) AS orderNum FROM erui_new_order.`order`, erui_new_order.`project` WHERE `order`.id = `project`.order_id AND `status` >= 3 AND `order`.delete_flag = 0
AND `project`.start_date BETWEEN #{startTime} AND #{endTime}
GROUP BY business_unit_id
UNION ALL
SELECT CASE WHEN org_id IN (9907) THEN 1 WHEN org_id IN (9969) THEN 2 WHEN org_id IN (9970) THEN 3 WHEN org_id IN (9928 , 9929, 9931, 9930 ) THEN 4 WHEN org_id IN (9904 , 9905, 9906, 9937) THEN 5 WHEN org_id IN (9902 , 9917, 9938) THEN 6 WHEN org_id IN (9903 , 9901, 9940) THEN 7 WHEN org_id IN (9899 , 9916) THEN 8 ELSE 100 END AS org_id, COUNT(1) AS inquiryNum, 0 AS orderNum FROM erui_rfq.inquiry as t1, (SELECT inquiry_id AS log_inquiry_id FROM erui_rfq.inquiry_check_log WHERE out_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id
HAVING MAX(out_at) BETWEEN #{startTime} AND #{endTime}
) as t4 WHERE t4.log_inquiry_id = t1.id AND t1.deleted_flag = 'N' AND t1.`status` IN ('MARKET_CONFIRMING' , 'QUOTE_SENT', 'INQUIRY_CLOSED') GROUP BY org_id) AS t1 GROUP BY t1.org_id ORDER BY 1
