mapper.select:
SELECT `year`, SUM(money) AS money, SUM(count) AS count FROM erui_report.rep_area_performance
`year` >= #{startYear}
AND `year` <= #{endYear}
GROUP BY `year` ORDER BY `year` ASC

SELECT area_bn, area_name, SUM(money) AS money, SUM(count) AS count FROM erui_report.rep_area_performance
`year` >= #{startYear}
AND `year` <= #{endYear}
GROUP BY area_bn , area_name

SELECT id, DATE_FORMAT(project_start_date, '%Y/%m/%d') AS projectStartDate, contract_no AS contractNo, order_type AS orderType, project_name AS projectName, exec_co_name AS execCoName, org_id AS orgId, org_name AS orgName, area_name AS areaName, area_bn AS areaBn, crm_code AS crmCode, money AS money, profit_percent AS profitPercent, profit AS profit, acquiring_user AS acquiringUser, business_name AS businessName FROM req_order_list
project_start_date >= #{startTime}
AND project_start_date <= #{endTime}
