mapper.select:
SELECT COUNT(1) AS inqCount , IFNULL(SUM(IFNULL(quotation_price,0.00)),0.00) AS inqAmount , SUM((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN 1 ELSE 0 END)) AS quoteCount, inquiry_area AS area, inquiry_unit AS country FROM erui_rfq.v_inquiry_count WHERE 1=1
and rollin_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
GROUP BY inquiry_area ,inquiry_unit HAVING area IS NOT NULL AND country IS NOT NULL;

SELECT COUNT(1) AS inqCount , IFNULL(SUM(IFNULL(quotation_price,0.00)),0.00) AS inqAmount , SUM((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN 1 ELSE 0 END)) AS quoteCount, IFNULL(AVG(IFNULL(quote_need_time,0)),0) AS quoteTime, organization AS org FROM erui_rfq.v_inquiry_count WHERE 1=1
and rollin_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
GROUP BY organization HAVING org IS NOT NULL ;
