mapper.select:
SELECT SUM(inqCount) AS inqCount, SUM(inqAmount) AS inqAmount, SUM(quoteCount) AS quoteCount, SUM(quoteAmount) AS quoteAmount, d.`name` AS area, b.`name` AS country FROM (SELECT 1 AS inqCount, IFNULL(t2.total_quote_price, 0) AS inqAmount, 0 AS quoteCount, 0 AS quoteAmount, t1.id AS inquiryId, t1.country_bn AS country_bn FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2, (SELECT inquiry_id AS log_inquiry_id, MAX(into_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE in_node = 'BIZ_DISPATCHING' GROUP BY inquiry_id) AS t3 WHERE t1.id = t2.inquiry_id AND t1.id = t3.log_inquiry_id
AND t3.log_create_at BETWEEN #{startTime} and #{endTime}
AND t1.deleted_flag = 'N' AND t1.`status` != 'DRAFT' UNION ALL SELECT 0 AS inqCount, 0 AS inqAmount, 1 AS quoteCount, IFNULL(t2.total_quote_price, 0) AS quoteAmount, t1.id AS inquiryId, t1.country_bn AS country_bn FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2, (SELECT inquiry_id AS log_inquiry_id, MAX(into_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE in_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id) AS t3 WHERE t1.id = t2.inquiry_id AND t2.deleted_flag = 'N' AND t1.deleted_flag = 'N' AND t1.`status` != 'DRAFT' AND t3.log_inquiry_id = t1.id
AND t3.log_create_at BETWEEN #{startTime} and #{endTime}
) as a LEFT JOIN `erui_dict`.`country` `b` ON `a`.`country_bn` = `b`.`bn` AND `b`.`lang` = 'zh' AND `b`.`deleted_flag` = 'N' LEFT JOIN `erui_operation`.`market_area_country` `c` ON `a`.`country_bn` = `c`.`country_bn` LEFT JOIN `erui_operation`.`market_area` `d` ON `c`.`market_area_bn` = `d`.`bn` AND `d`.`lang` = 'zh' AND `d`.`deleted_flag` = 'N' GROUP BY d.`name` , b.`name` HAVING d.`name` IS NOT NULL AND b.`name` IS NOT NULL

SELECT t2.`name`, COUNT(1) AS inqCount, SUM(IFNULL(t3.total_quote_price, 0)) AS inqAmount FROM `erui_rfq`.`inquiry` t1 LEFT JOIN `erui_rfq`.`final_quote` t3 ON t1.id = t3.inquiry_id AND t3.deleted_flag = 'N' LEFT JOIN erui_rfq.inquiry_check_log t4 ON t1.id = `t4`.`inquiry_id` AND t4.in_node = 'MARKET_APPROVING', `erui_dict`.`country` t2 WHERE t1.`deleted_flag` = 'N' AND t1.`status` <> 'DRAFT' and t1.`country_bn` = `t2`.`bn` AND `t2`.`lang` = 'zh' and quote_status in ('QUOTED','COMPLETED')
AND `t4`.`created_at` BETWEEN #{startTime} and #{endTime}
GROUP BY t2.bn , t2.`name`;

SELECT t2.`name`, COUNT(1) AS quoteCount, SUM(IFNULL(t3.total_quote_price, 0)) AS quoteAmount FROM `erui_rfq`.`inquiry` t1 LEFT JOIN `erui_rfq`.`final_quote` t3 ON t1.id = t3.inquiry_id AND t3.deleted_flag = 'N' LEFT JOIN erui_rfq.inquiry_check_log t4 ON t1.id = `t4`.`inquiry_id` AND t4.in_node = 'MARKET_APPROVING', `erui_dict`.`country` t2 WHERE t1.`deleted_flag` = 'N' AND t1.`status` <> 'DRAFT' and t1.`country_bn` = `t2`.`bn` AND `t2`.`lang` = 'zh' and quote_status in ('QUOTED','COMPLETED')
AND `t4`.`created_at` BETWEEN #{startTime} and #{endTime}
GROUP BY t2.bn , t2.`name`;

SELECT SUM(inqCount) AS inqCount, SUM(inqAmount) AS inqAmount, SUM(quoteCount) AS quoteCount, SUM(quoteAmount) AS quoteAmount, ifnull(SUM(quoteTime) / SUM(quoteCount),0) AS quoteTime, e.`name` AS org FROM (SELECT 1 AS inqCount, IFNULL(t2.total_quote_price, 0) AS inqAmount, 0 AS quoteCount, 0 AS quoteAmount, t1.id AS inquiryId, t1.org_id AS org_id, 0 AS quoteTime FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2, (SELECT inquiry_id AS log_inquiry_id, MAX(into_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE in_node = 'BIZ_DISPATCHING' GROUP BY inquiry_id) AS t3 WHERE t1.id = t2.inquiry_id AND t1.id = t3.log_inquiry_id AND t1.deleted_flag = 'N'
AND t3.log_create_at BETWEEN #{startTime} and #{endTime}
AND t1.`status` != 'DRAFT' UNION ALL SELECT 0 AS inqCount, 0 AS inqAmount, 1 AS quoteCount, IFNULL(t2.total_quote_price, 0) AS quoteAmount, t1.id AS inquiryId, t1.org_id AS org_id, (UNIX_TIMESTAMP(`t3`.`log_create_at`) - UNIX_TIMESTAMP(`t1`.`created_at`)) / 3600 AS quoteTime FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2, (SELECT inquiry_id AS log_inquiry_id, MAX(into_at) AS log_create_at FROM erui_rfq.inquiry_check_log WHERE in_node = 'MARKET_CONFIRMING' GROUP BY inquiry_id) AS t3 WHERE t1.id = t2.inquiry_id AND t2.deleted_flag = 'N' AND t1.deleted_flag = 'N' AND t1.`status` != 'DRAFT' AND t3.log_inquiry_id = t1.id
AND t3.log_create_at BETWEEN #{startTime} and #{endTime}
) AS a LEFT JOIN `erui_sys`.`org` `e` ON `a`.`org_id` = `e`.`id` AND `e`.`deleted_flag` = 'N' GROUP BY `e`.`name` HAVING `e`.`name` IS NOT NULL

SELECT t1.category, COUNT(DISTINCT t2.id) AS num FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, erui_rfq.inquiry_check_log t3 WHERE t1.inquiry_id = t2.id AND t1.inquiry_id = t3.inquiry_id AND t3.in_node = 'MARKET_APPROVING' AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != '' AND t2.`status` IN ('BIZ_DISPATCHING' , 'CC_DISPATCHING', 'BIZ_QUOTING', 'LOGI_DISPATCHING', 'LOGI_QUOTING', 'LOGI_APPROVING', 'BIZ_APPROVING', 'MARKET_APPROVING', 'MARKET_CONFIRMING', 'QUOTE_SENT', 'INQUIRY_CLOSED')
AND t3.created_at BETWEEN #{startTime} and #{endTime}
GROUP BY t1.category ORDER BY 2 DESC

SELECT t1.category, SUM(t4.total_quote_price)/10000 AS num FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, erui_rfq.inquiry_check_log t3, erui_rfq.final_quote t4 WHERE t1.inquiry_id = t2.id AND t1.inquiry_id = t3.inquiry_id AND t3.in_node = 'MARKET_APPROVING' AND t2.id = t4.inquiry_id AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t4.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != '' AND t2.`status` IN ('BIZ_DISPATCHING' , 'CC_DISPATCHING', 'BIZ_QUOTING', 'LOGI_DISPATCHING', 'LOGI_QUOTING', 'LOGI_APPROVING', 'BIZ_APPROVING', 'MARKET_APPROVING', 'MARKET_CONFIRMING', 'QUOTE_SENT', 'INQUIRY_CLOSED')
AND t3.created_at BETWEEN #{startTime} and #{endTime}
GROUP BY t1.category ORDER BY 2 DESC

SELECT t1.category, COUNT(DISTINCT t4.id) AS num FROM erui_rfq.inquiry_item t1, erui_rfq.inquiry t2, erui_rfq.inquiry_check_log t3, erui_rfq.final_quote t4 WHERE t1.inquiry_id = t2.id AND t1.inquiry_id = t3.inquiry_id AND t2.id = t4.inquiry_id AND t3.in_node = 'MARKET_APPROVING' AND t1.deleted_flag = 'N' AND t2.deleted_flag = 'N' AND t4.deleted_flag = 'N' AND t1.category IS NOT NULL AND t1.category != '' AND t2.`status` IN ('MARKET_CONFIRMING' , 'QUOTE_SENT', 'INQUIRY_CLOSED')
AND t3.created_at BETWEEN #{startTime} and #{endTime}
GROUP BY t1.category ORDER BY 2 DESC

SELECT a.inqCount,a.inqAmount,a.category ,IFNULL(b.quoteCount,0) AS quoteCount ,IFNULL(b.quoteAmount,0.00) AS quoteAmount FROM ( SELECT IFNULL(SUM(IFNULL(cate_count,0)),0) AS inqCount, IFNULL(SUM(IFNULL(cate_count*quote_unit_price,0.0)),0.00) AS inqAmount, pro_category AS category FROM erui_rfq.v_inquiry_sku2 left join ( SELECT inquiry_id as log_inquiry_id,max(created_at) as log_create_at FROM erui_rfq.inquiry_check_log where in_node = 'MARKET_APPROVING' group by inquiry_id ) as z ON erui_rfq.v_inquiry_sku2.inquiry_id = `z`.`log_inquiry_id` WHERE 1=1
and `z`.`log_create_at` BETWEEN #{startTime} and #{endTime}
GROUP BY pro_category HAVING category IS NOT NULL ) a LEFT JOIN ( SELECT IFNULL(SUM(IFNULL(cate_count,0)),0) AS quoteCount, IFNULL(SUM(IFNULL(cate_count*quote_unit_price,0.0)),0.00) AS quoteAmount, pro_category AS category FROM erui_rfq.v_inquiry_sku2 left join ( SELECT inquiry_id as log_inquiry_id,max(created_at) as log_create_at FROM erui_rfq.inquiry_check_log where in_node = 'MARKET_APPROVING' group by inquiry_id ) as z ON erui_rfq.v_inquiry_sku2.inquiry_id = `z`.`log_inquiry_id` WHERE 1=1
and `z`.`log_create_at` BETWEEN #{startTime} and #{endTime}
and quotation_num in (SELECT serial_no FROM erui_rfq.inquiry WHERE quote_status in ('QUOTED' , 'COMPLETED')) GROUP BY pro_category HAVING category IS NOT NULL ) b ON a.category=b.category

SELECT COUNT(1) as visitCount , FROM_DAYS(TO_DAYS(datetime)) as visitAt ,area FROM ( SELECT visit.buyer_id AS buyerId ,visit.created_at AS datetime, areasZh.name as area FROM erui_buyer.buyer_visit AS visit LEFT JOIN erui_buyer.buyer AS buyers ON visit.buyer_id=buyers.id LEFT JOIN erui_operation.market_area_country AS areasBn on buyers.country_bn=areasBn.country_bn LEFT JOIN erui_operation.market_area AS areasZh ON areasBn.market_area_bn=areasZh.bn AND areasZh.lang='zh' ) AS a WHERE 1=1
and datetime BETWEEN DATE_FORMAT(#{startTime},"%Y-%m-%d") and DATE_FORMAT(#{endTime},"%Y-%m-%d")
GROUP BY TO_DAYS(datetime),area HAVING area is not NULL AND visitAt IS NOT NULL ;
