mapper.select:
SELECT COUNT(1) AS inqCount , IFNULL(SUM(IFNULL(quotation_price,0.00)),0.00) AS inqAmount , SUM((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN 1 ELSE 0 END)) AS quoteCount, SUM(IFNULL((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN quotation_price ELSE 0.00 END),0.00)) AS quoteAmount, inquiry_area AS area, inquiry_unit AS country FROM erui_rfq.v_inquiry_count WHERE 1=1
and rollin_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
GROUP BY inquiry_area ,inquiry_unit HAVING area IS NOT NULL AND country IS NOT NULL;

SELECT COUNT(1) AS inqCount , IFNULL(SUM(IFNULL(quotation_price,0.00)),0.00) AS inqAmount , SUM((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN 1 ELSE 0 END)) AS quoteCount, SUM(IFNULL((CASE WHEN quoted_status ='已报价' OR quoted_status='已完成' THEN quotation_price ELSE 0.00 END),0.00)) AS quoteAmount, IFNULL(AVG(IFNULL(quote_need_time,0)),0) AS quoteTime, organization AS org FROM erui_rfq.v_inquiry_count WHERE 1=1
and rollin_time BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s")
GROUP BY organization HAVING org IS NOT NULL ;

SELECT COUNT(1) as visitCount , FROM_DAYS(TO_DAYS(datetime)) as visitAt ,area FROM ( SELECT visit.buyer_id AS buyerId ,visit.created_at AS datetime, areas.market_area_bn as area FROM erui_buyer.buyer_visit AS visit LEFT JOIN erui_sys.country_member AS member ON visit.created_by =member.employee_id LEFT JOIN erui_operation.market_area_country AS areas on member.country_bn=areas.country_bn ) AS a WHERE 1=1
and datetime BETWEEN DATE_FORMAT(#{startTime},"%Y-%m-%d") and DATE_FORMAT(#{endTime},"%Y-%m-%d")
GROUP BY TO_DAYS(datetime),area HAVING area is not NULL AND visitAt IS NOT NULL ;
