mapper.select:
SELECT tb1.id, tb1.buyer_code, tb1.country_bn, tb4.`name` AS countryName, tb1.area_bn, tb3.`name` AS areaName, tb1.created_at, tb2.maxQuotePrice, tb2.totalQuotePrice FROM (SELECT t1.*, t2.total_quote_price FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2 WHERE t1.id = t2.inquiry_id AND t1.`status` = 'QUOTE_SENT'
AND t1.created_at BETWEEN #{startTime} AND #{endTime}) AS tb1,
(SELECT t1.buyer_code, t1.country_bn, t1.area_bn, MAX(t2.total_quote_price) AS maxQuotePrice, SUM(t2.total_quote_price) AS totalQuotePrice FROM erui_rfq.inquiry t1, erui_rfq.final_quote t2 WHERE t1.id = t2.inquiry_id AND t1.`status` = 'QUOTE_SENT'
AND t1.created_at BETWEEN #{startTime} AND #{endTime}
GROUP BY buyer_code , country_bn , area_bn) AS tb2 LEFT JOIN erui2_operation.market_area tb3 ON tb2.area_bn = tb3.bn AND tb3.lang = 'zh' LEFT JOIN erui_dict.country tb4 ON tb2.country_bn = tb4.bn AND tb4.lang = 'zh' WHERE tb1.buyer_code = tb2.buyer_code AND tb1.country_bn = tb2.country_bn AND tb1.area_bn = tb2.area_bn AND tb1.total_quote_price = tb2.maxQuotePrice
AND tb1.country_bn = #{countryBn}
AND tb1.area_bn = #{areaBn}
AND tb1.buyer_code LIKE '%${buyerCode}%'

SELECT tb1.buyer_code, tb2.`name` AS countryName, tb1.country_bn, tb1.area_bn, tb3.`name` AS areaName, tb1.cycle FROM (SELECT t1.buyer_code, t1.country_bn, t1.area_bn, CASE WHEN COUNT(1) > 1 THEN DATEDIFF(MAX(t1.created_at), MIN(t1.created_at)) / COUNT(1) ELSE 0 END AS cycle FROM erui_rfq.inquiry t1 WHERE t1.`status` = 'QUOTE_SENT'
AND t1.created_at BETWEEN #{startTime} AND #{endTime}
GROUP BY buyer_code , country_bn , area_bn) AS tb1 LEFT JOIN erui_operation.market_area tb2 ON tb1.area_bn = tb2.bn AND tb2.lang = 'zh' LEFT JOIN erui_dict.country tb3 ON tb1.country_bn = tb3.bn AND tb3.lang = 'zh' where 1=1
AND tb1.country_bn = #{countryBn}
AND tb1.area_bn = #{areaBn}
AND tb1.buyer_code LIKE '%${buyerCode}%'
