mapper.select:
SELECT COUNT(1) AS registerCount ,area ,level,buyerType FROM ( SELECT buyers.buyer_code AS buyerNo,buyers.id AS buyerId , (case when (buyers.`source` = 1) then '后台' when (buyers.`source` = 2) then '门户' when (buyers.`source` = 3) then '移动端' end) AS `source`, (case when (buyers.`buyer_level` = 52) then '普通会员' when (buyers.`buyer_level` = 53) then '高级会员' end) AS `level`, buyers.level_at AS level_at , areasBn.country_bn AS country,
(CASE WHEN areasBn.country_bn ='China' THEN '中国' ELSE areasZh.name END ) as area, crmType.buyerStatus AS buyerType FROM erui_buyer.buyer AS buyers LEFT JOIN erui_operation.market_area_country AS areasBn on buyers.country_bn=areasBn.country_bn LEFT JOIN erui_operation.market_area AS areasZh ON areasBn.market_area_bn=areasZh.bn AND areasZh.lang='zh' LEFT JOIN ( SELECT crmCode, (CASE WHEN COUNT(1)=2 THEN 'ERUIANDKERUI' WHEN COUNT(1)=1 AND order_belongs=1 THEN 'ERUI' ELSE 'KERUI' END) AS buyerStatus FROM ( SELECT crm_code crmCode , order_belongs FROM erui_order.`order` WHERE order_category != 1 AND order_category != 3 AND crm_code IS NOT NULL AND crm_code <> '' AND order_belongs IS NOT NULL AND order_belongs <> '' GROUP BY crm_code,order_belongs ) AS t group by t.crmCode
) AS crmType ON buyers.buyer_code=crmType.crmCode WHERE buyers.deleted_flag='N' ) as a WHERE level IN ('普通会员','高级会员') and level_at BETWEEN DATE_FORMAT(#{startTime},"%Y/%m/%d %H:%i:%s") and DATE_FORMAT(#{endTime},"%Y/%m/%d %H:%i:%s") GROUP BY area ,level,buyerType HAVING area IS NOT NULL AND level IS NOT NULL AND buyerType IS NOT NULL
